import "./App.css";
import {useState, useEffect} from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import TodayCard from './TodayCard';


const api = {
	key: "ed94ec84f5cd47a1bcf164348231509",
	base: "https://api.weatherapi.com/v1/forecast.json",
	days: "6"
};

function App() {
	const [search, setSearch] = useState("");
	const [weather, setWeather] = useState({});

	/*

  */
	const searchPressed = () => {
		fetch(`${
			api.base
		}?key=${
			api.key
		}&q=${search}&days=${
			api.days
		}&aqi=yes&alerts=no`).then((res) => res.json()).then((result) => {
			setWeather(result);
		});


	};

	// console.log(weather)

	return (
		<div className="App">
			<div class="container">
				<div class="jumbotron">
					<h1>Weather App</h1>
				</div>

				<div>
					<input type="text" placeholder="Enter city/town..."onChange={(e) => setSearch(e.target.value)}/>
					<button onClick={searchPressed}>Search</button>
				</div>
				<Container>
					<Row>
						<Col lg={12}>
							<span><TodayCard/></span>
						</Col>
					</Row>
					<Row>
						<Col md>

							md


						</Col>
						<Col md>

							md

						</Col>
						<Col md>
							md


						</Col>
					</Row>
				</Container>
			</div>
		</div>

	);
}

export default App;
