import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';

const Weather = () => {
  const [searchCity, setSearchCity] = useState('Paris'); // Default search criteria
  const [weatherData, setWeatherData] = useState(null);
  const [isCelsius, setIsCelsius] = useState(true);
  const [error, setError] = useState(null);
  const [searchSubmitted, setSearchSubmitted] = useState(false);

  const handleSearch = () => {
    setError(null); // Reset error state
    setSearchSubmitted(true); // Mark search as submitted
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=ed94ec84f5cd47a1bcf164348231509&q=${searchCity}&days=5&aqi=yes&alerts=no`)
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setError(data.error.message); // Handle API error response
        } else {
          setWeatherData(data);
        }
      })
      .catch((error) => {
        setError('Error fetching weather data. Please try again later.'); // Handle network or other errors
        console.error('Error fetching weather data:', error);
      });
  };

  useEffect(() => {
    // Fetch data for the default city (e.g., Paris) on component mount
    handleSearch();
  }, []);

  // Condition to prevent onClick execution before search submission
  const handleButtonClick = () => {
    if (searchSubmitted) {
      handleSearch();
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!weatherData) {
    return <div>Loading...</div>;
  }

  const currentWeather = weatherData.current || {};
  const forecastDays = (weatherData.forecast && weatherData.forecast.forecastday) || [];

  const toggleUnits = () => {
    setIsCelsius(!isCelsius);
  };

  return (
    <Container>
      <h1 className="mt-4">Weather App</h1>
      <Form className="mt-4 mb-4">
        <Form.Group controlId="formCity">
          <Form.Control
            type="text"
            placeholder="Enter a city"
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleButtonClick}>
          Search
        </Button>
      </Form>
      <h2 className="mt-4">Today's Weather in {weatherData.location?.name}</h2>
      <Row>
        <Col>
          <Card bg="primary" text="white" className="mb-4">
            <Card.Body>
              <Card.Title>Today</Card.Title>
              <Card.Text>
                Temperature: {isCelsius ? currentWeather.temp_c + '°C' : currentWeather.temp_f + '°F'}<br />
                Condition: {currentWeather.condition?.text}<br />
                Humidity: {currentWeather.humidity}%
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Form.Check
        type="switch"
        id="toggleUnits"
        label={`Toggle to ${isCelsius ? 'Fahrenheit' : 'Celsius'}`}
        onChange={toggleUnits}
        className="mb-3"
        disabled={!searchSubmitted} // Disable unit toggle until search is submitted
      />
      <h2>5-Day Weather Forecast for {weatherData.location?.name}</h2>
      <Row>
        {forecastDays.map((day, index) => (
          <Col key={index} sm={6} md={4} lg={3}>
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>{day.date}</Card.Title>
                <Card.Text>
                  Max Temperature: {isCelsius ? day.day.maxtemp_c + '°C' : day.day.maxtemp_f + '°F'}<br />
                  Min Temperature: {isCelsius ? day.day.mintemp_c + '°C' : day.day.mintemp_f + '°F'}<br />
                  Condition: {day.day.condition?.text}<br />
                  Humidity: {day.day.avghumidity}%
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Weather;
