import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';



const Weather = () => {

    
  const [searchCity, setSearchCity] = useState('Paris');
  const [weather, setWeather] = useState(null);
  const [isCelsius, setIsCelsius] = useState(true);

  const api = {
    key: "ed94ec84f5cd47a1bcf164348231509",
    base: "https://api.weatherapi.com/v1/forecast.json",
    days: "6"
};

const searchPressed = () => {
    fetch(`${
        api.base
    }?key=${
        api.key
    }&q=${searcCity}&days=${
        api.days
    }&aqi=yes&alerts=no`).
    then((res) => res.json())
    .then((result) => {
        setWeather(result)
    .catch((error) => console.error('Error fetching weather data:', error));
    });
/*   const handleSearch = () => {
    
    fetch(`${api.base}?${api.key}&q=${api.searchCity}&${api.days}&aqi=yes`)
      .then((response) => response.json())
      .then((data) => setweather(data))
      .catch((error) => console.error('Error fetching weather data:', error));
  }; */

  useEffect(() => {
    //handleSearch();
  }, []);

  if (!weather) {
    return <div>Loading...</div>;
  }

  //const currentWeather = weather.current;
  //const forecastDays = weather.forecast.forecastday;

  const toggleUnits = () => {
    setIsCelsius(!isCelsius);
  };

  return (
    <Container>
      <h1 className="mt-4">Weather App</h1>
      <Form className="mt-4 mb-4">
        <Form.Group controlId="formCity">
          <Form.Control
            type="text"
            placeholder="Enter a city"
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={searchPressed}>
          Search
        </Button>
      </Form>
{/*       <h2 className="mt-4">Today's Weather in {weather.location.name}</h2> */}
      <Row>
        <Col>
          <Card bg="primary" text="white" className="mb-4">
            <Card.Body>
              <Card.Title>Today</Card.Title>
              <Card.Text>
{/*                 Temperature: {isCelsius ? currentWeather.temp_c + '°C' : currentWeather.temp_f + '°F'}<br />
                Condition: {currentWeather.condition.text}<br />
                Humidity: {currentWeather.humidity}% */}
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Form.Check
        type="switch"
        id="toggleUnits"
        label={`Toggle to ${isCelsius ? 'Fahrenheit' : 'Celsius'}`}
        onChange={toggleUnits}
        className="mb-3"
      />
     {/*  <h2>5-Day Weather Forecast for {weather.location.name}</h2>
      <Row>
        {forecastDays.map((day, index) => (
          <Col key={index} sm={6} md={4} lg={3}>
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>{day.date}</Card.Title>
                <Card.Text>
                  Max Temperature: {isCelsius ? day.day.maxtemp_c + '°C' : day.day.maxtemp_f + '°F'}<br />
                  Min Temperature: {isCelsius ? day.day.mintemp_c + '°C' : day.day.mintemp_f + '°F'}<br />
                  Condition: {day.day.condition.text}<br />
                  Humidity: {day.day.avghumidity}%
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row> */}
    </Container>
  );
};

} 
export default Weather;
