import "./App.css";
import React, {useState, useEffect} from "react";
import {Container, Row, Col, Card} from "react-bootstrap";


const api = {
	key: "ed94ec84f5cd47a1bcf164348231509",
	base: "https://api.weatherapi.com/v1/forecast.json",
	days: "6"
};

function App() {
	const [search, setSearch] = useState("");
	const [weather, setWeather] = useState({});

	/*

  */
	const searchPressed = () => {
		fetch(`${
			api.base
		}?key=${
			api.key
		}&q=${search}&days=${
			api.days
		}&aqi=yes&alerts=no`).then((res) => res.json()).then((result) => {
			setWeather(result);
		});


	};

	// console.log(weather)

	return (
		<div className="App">
			<div className="container">
				<div className="jumbotron">
					<h1>Weather App</h1>
				</div>
				<Container>
        <div>
					<input type="text" placeholder="Enter city/town..."
						onChange={(e) => setSearch(e.target.value)}/>
					<button onClick={searchPressed}>Search</button>
				</div>
					<Row>
						<Col lg={12}>
							<Card className="text-center">
								<Card.Header>Today</Card.Header>
								<Card.Body>
									<Card.Text> 
                    {/* If weather is not undefined display results from API */}
										{
										typeof weather.current !== "undefined" ? (
											<div> {/* Location  */}
												{
													weather.location.name +" "
												}

												{/* Temperature Celsius  */}
												{
													 weather.current.temp_c +" "
												} Â°C


											</div>
										) : ("")
									} </Card.Text>
								</Card.Body>
							</Card>

						</Col>
					</Row>
					<Row>
						<Col md>

                 1 of 3


						</Col>
						<Col md>

							2 of 3

						</Col>
						<Col md>
              3 of 3

						</Col>
					</Row>
				</Container>
			</div>
		</div>

	);
}

export default App;
