import "./App.css";
import {useState, useEffect} from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const api = {
	key: "ed94ec84f5cd47a1bcf164348231509",
	base: "https://api.weatherapi.com/v1/forecast.json",
	days: "6"
};

function App() {
	const [search, setSearch] = useState("");
	const [weather, setWeather] = useState({});

	/*

  */
	const searchPressed = () => {
		fetch(`${
			api.base
		}?key=${
			api.key
		}&q=${search}&days=${
			api.days
		}&aqi=yes&alerts=no`).then((res) => res.json()).then((result) => {
			setWeather(result);
		});


	};

	// console.log(weather)

	return (

		<div className="App">
    <Container>
    <Jumbotron>
      <h1>Weather App</h1>

    </Jumbotron>
    </Container>
    <Container>
        <Row>
          <Col lg={12}>lg=12</Col>
        </Row>
        <Row>
          <Col sm>sm=true</Col>
          <Col sm>sm=true</Col>
          <Col sm>sm=true</Col>
        </Row>
      </Container>
      </div>

	);


}

export default App;
