import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';

const Weather = () => {
  const [searchCity, setSearchCity] = useState('');
  const [weather, setweather] = useState(null);

  const handleSearch = () => {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=ed94ec84f5cd47a1bcf164348231509&q=${searchCity}&days=5&aqi=yes&alerts=no`)
      .then((response) => response.json())
      .then((data) => setweather(data))
      .catch((error) => console.error('Error fetching weather data:', error));
  };

  useEffect(() => {
    // Fetch data for a default city (e.g., Paris) on component mount
    handleSearch();
  }, []);

  if (!weather) {
    return <div>Loading...</div>;
  }

  const currentWeather = weather.current;
  const forecastDays = weather.forecast.forecastday;

  return (
    <Container>
      <h1 className="mt-4">Weather App</h1>
      <Form className="mt-4 mb-4">
        <Form.Group controlId="formCity">
          <Form.Control
            type="text"
            placeholder="Enter a city"
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleSearch}>
          Search
        </Button>
      </Form>
      <h2 className="mt-4">Today's Weather in {weather.location.name}</h2>
      <Row>
        <Col>
          <Card bg="primary" text="white" className="mb-4">
            <Card.Body>
              <Card.Title>Today</Card.Title>
              <Card.Text>
                Temperature: {currentWeather.temp_c}°C<br />
                Condition: {currentWeather.condition.text}<br />
                Humidity: {currentWeather.humidity}%
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <h2>5-Day Weather Forecast for {weather.location.name}</h2>
      <Row>
        {forecastDays.map((day, index) => (
          <Col key={index} sm={6} md={4} lg={3}>
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>{day.date}</Card.Title>
                <Card.Text>
                  Max Temperature: {day.day.maxtemp_c}°C<br />
                  Min Temperature: {day.day.mintemp_c}°C<br />
                  Condition: {day.day.condition.text}<br />
                  Humidity: {day.day.avghumidity}%
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Weather;
