import "./App.css";
import React, {useState} from "react";
import {Container, Row, Col, Card, Navbar} from "react-bootstrap";


const api = {
	key: "ed94ec84f5cd47a1bcf164348231509",
	base: "https://api.weatherapi.com/v1/forecast.json",
	days: "6"
};

function App() {
	const [search, setSearch] = useState("");
	const [weather, setWeather] = useState({});

	/*

  */
	const searchPressed = () => {
		fetch(`${
			api.base
		}?key=${
			api.key
		}&q=${search}&days=${
			api.days
		}&aqi=yes&alerts=no`).then((res) => res.json()).then((result) => {
			console.log(result)

			var jsonData = JSON.parse(result)
			console.log(jsonData)
			setWeather(jsonData);
		});

	};



	return (
		<div className="App">
      <div className="jumbotron">
					<h1>Weather App</h1>
				</div>
			<div className="container">
				<Container>
        <Navbar> 
					<input type="text" placeholder="Enter city/town..."onChange={(e) => setSearch(e.target.value)}/>
					<button onClick={searchPressed}>Search</button>
				</Navbar>
					<Row>
						<Col lg={12}>
							<Card className="text-center">
								<Card.Header>{weather.current}</Card.Header>
								<Card.Body>
									<Card.Text> 
                    {/* If weather is not undefined display results from API */}
										{typeof weather.current !== "undefined" ? (
											<div> 


												<img src={weather.current.condition.icon}></img>

												<p>{weather.location.name}</p>

												<p>{weather.current.temp_c}°C</p>

												<p>{weather.current.humidity}</p>
											</div>
										) : ("")
									} </Card.Text>
								</Card.Body>
							</Card>

						</Col>
					</Row>
					<Row>
						<Col md={3}>
{/* 							<div>
								<p>{weather.forecast.localtime}</p>

								<img src={weather.current.condition.icon}></img>

								<p>{weather.location.name}</p>

								<p>{weather.current.temp_c}°C</p>

								<p>{weather.current.humidity}</p>
							</div> */}
						</Col>
						<Col md={3}>
{/* 							<div>
							<img src={weather.forecast.forecastday.condition.icon}></img>

							<p>{weather.location.name}</p>

							<p>{weather.forecast.forecastday.temp_c}°C</p>

							<p>{weather.forecast.forecastday.humidity}</p>

							</div> */}
						</Col>
						<Col md={3}>
              				3 of 3
						</Col>
					</Row>
				</Container>
			</div>
		</div>

	);
}

export default App;
