import "./App.css";
import { useState, useEffect} from "react";

const api = {
  key: "ed94ec84f5cd47a1bcf164348231509",
  base: "https://api.weatherapi.com/v1/forecast.json",
  days: "6"
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});

  /*

  */
  const searchPressed = () => {
    fetch(`${api.base}?key=${api.key}&q=${search}&days=${api.days}&aqi=yes&alerts=no`)
      .then((res) => res.json())
      .then((result) => {
        setWeather(result);
      });


  };

<div className="App">
	<div class="container">
		<div class="jumbotron">
			<h1>Weather App</h1>
		</div>

		<div>
			<input type="text" placeholder="Enter city/town..."
				onChange={
					(e) => setSearch(e.target.value)
				}/>
			<button onClick={searchPressed}>Search</button>
		</div>
		<Container>
			<Row>
				<Col lg={12}>
					<Card className="text-center">
						<Card.Header>Today</Card.Header>
						<Card.Body>
							<Card.Title>Special title treatment</Card.Title>
							<Card.Text> {/* If weather is not undefined display results from API */}
								{
								typeof weather.current !== "undefined" ? (
									<div> {/* Location  */}
										<p>{weather.location.name}</p>

										{/* Temperature Celsius  */}
										<p>{weather.current.temp_c}Â°C</p>
									</div>
								) : ("")
							} </Card.Text>
						</Card.Body>
						<Card.Footer className="text-muted">2 days ago</Card.Footer>
					</Card>

				</Col>
			</Row>
			<Row>
				<Col md>

					md


				</Col>
				<Col md>

					md

				</Col>
				<Col md>
					md


				</Col>
			</Row>
		</Container>
	</div>
</div>
}

export default App;
