import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';

const Weather = () => {
  const [searchCity, setSearchCity] = useState('');
  const [weatherData, setWeatherData] = useState();
  const [isCelsius, setIsCelsius] = useState(true);

  const currentWeather = weatherData.current;
  const forecastday = weatherData.forecast.forecastday; // Check if forecast exists
  
  const handleSearch = () => {
    fetch(`http://api.weatherapi.com/v1/current.json?key=ed94ec84f5cd47a1bcf164348231509&q=${searchCity}&days=6&aqi=yes&alerts=no`)
      .then((response) => response.json())
      .then((data) => setWeatherData(data))
      .catch((error) => console.error('Error fetching weather data:', alert(error))
      );
      
  };

  useEffect(() => {
    // Fetch data for a default city (e.g., Paris) on component mount
    handleSearch();
    console.log(weatherData)
   
    if (weatherData) {
        return <div>Loading...</div>;
      }

  }, [weatherData]);



  console.log(weatherData.current);

  
  if (!forecastday ) {
    return <div>No forecast data available.</div>;
  }else if(!currentWeather){
    return <div>No Weather data available.</div>;
  }else if(!forecastday && currentWeather){
    <p>There is no data</p>
  }

  const toggleUnits = () => {
    setIsCelsius(!isCelsius);
  };

  return (
    <Container>
      <h1 className="mt-4">Weather App</h1>
      <Form className="mt-4 mb-4">
        <Form.Group controlId="formCity">
          <Form.Control
            type="text"
            placeholder="Enter a city"
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleSearch}>
          Search
        </Button>
      </Form>
      <h2 className="mt-4">Today's Weather in {weatherData.location.date}</h2>
      <Row>
        <Col>
          <Card bg="primary" text="white" className="mb-4">
            <Card.Body>
              <Card.Title>Today</Card.Title>
              <Card.Text>
                Temperature: {isCelsius ? currentWeather.temp_c + '°C' : currentWeather.temp_f + '°F'}<br />
                Condition: {currentWeather.condition.text}<br />
                Humidity: {currentWeather.humidity}%
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Form.Check
        type="switch"
        id="toggleUnits"
        label={`Toggle to ${isCelsius ? 'Fahrenheit' : 'Celsius'}`}
        onChange={toggleUnits}
        className="mb-3"
      />
      <h2>5-Day Weather Forecast for {weatherData.location.name}</h2>
      <Row>
        {forecastday.map((day, index) => (
          <Col key={index} sm={6} md={4} lg={3}>
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>{day.date}</Card.Title>
                <Card.Text>
                  Max Temperature: {isCelsius ? day.day.maxtemp_c + '°C' : day.day.maxtemp_f + '°F'}<br />
                  Min Temperature: {isCelsius ? day.day.mintemp_c + '°C' : day.day.mintemp_f + '°F'}<br />
                  Condition: {day.day.condition.text}<br />
                  Humidity: {day.day.avghumidity}%
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Weather;
