import "./App.css";
import { useState, useEffect} from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const api = {
  key: "ed94ec84f5cd47a1bcf164348231509",
  base: "https://api.weatherapi.com/v1/forecast.json",
  days: "6"
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});

  /*

  */
  const searchPressed = () => {
    fetch(`${api.base}?key=${api.key}&q=${search}&days=${api.days}&aqi=yes&alerts=no`)
      .then((res) => res.json())
      .then((result) => {
        setWeather(result);
      });


  };

  //console.log(weather)

  return (



    <Container>
      <Row>
        <Col>1 of 2</Col>
      </Row>
      <Row>
        <Col>1 of 3</Col>
        <Col>2 of 3</Col>
        <Col>3 of 3</Col>
      </Row>
    </Container>
  );





}

export default App;
